steps.run:
git config --local user.email ${{ steps.python_script.outputs.commit_email }}
git config --local user.name ${{ steps.python_script.outputs.commit_username }}
git add .github/scripts/listings.json
git add README.md
git add README-Off-Season.md
git diff-index --quiet HEAD || (git commit -a -m "${{ steps.python_script.outputs.commit_message }}" --allow-empty)

cat << 'EOF' > comment.txt
${{ steps.python_script.outputs.summary_comment }}
EOF
gh issue comment ${{ github.event.issue.number }} --body-file comment.txt
gh issue close --comment "✅ Bulk update complete! The README has been updated and changes have been committed. Auto-closing issue." ${{ github.event.issue.number }}
